# Definindo as classes

class Venda:
    def __init__(self, nf, st, cfop, emissao, vendedor, razao_social_cliente, uf, cod_mat, descricao_material, unid_medida, quantidade, valor_unitario, valor_total):
        self.nf = nf
        self.st = st
        self.cfop = cfop
        self.emissao = emissao
        self.vendedor = vendedor
        self.razao_social_cliente = razao_social_cliente
        self.uf = uf
        self.cod_mat = cod_mat  # Mantendo como string
        self.descricao_material = descricao_material
        self.unid_medida = unid_medida
        self.quantidade = quantidade
        self.valor_unitario = valor_unitario
        self.valor_total = valor_total

    def __str__(self):
        return f"NF: {self.nf}, Cliente: {self.razao_social_cliente}, Material: {self.descricao_material}, Quantidade: {self.quantidade}, Valor Total: {self.valor_total}"

class Estoque:
    def __init__(self):
        self.produtos = {}

    def adicionar_produto(self, produto):
        if produto.cod_mat not in self.produtos:
            self.produtos[produto.cod_mat] = {
                "descricao": produto.descricao_material,
                "quantidade_total": 0,
                "valor_unitario": produto.valor_unitario,
                "valor_total": 0,
                "data": produto.emissao  # Adicionando a data da emissão
            }
        self.produtos[produto.cod_mat]["quantidade_total"] += produto.quantidade
        self.produtos[produto.cod_mat]["valor_total"] += produto.valor_total

    def realizar_comparacao(self, cod_mat):
        if cod_mat in self.produtos:
            produto = self.produtos[cod_mat]
            print(f"Produto: {produto['descricao']}")
            print(f"Quantidade Total: {produto['quantidade_total']} unidades")
            print(f"Valor Total: R$ {produto['valor_total']:,.2f}")
            # Determinando manutenção preventiva (exemplo simples: manutenção se a quantidade for menor que 10)
            if produto["quantidade_total"] < 10:
                print("Manutenção preventiva necessária!")
            else:
                print("Produto em estoque suficiente.")
        else:
            print("Produto não encontrado no estoque.")
    
    def verificar_manutencao_preventiva(self):
        print("\n=== Produtos que precisam de manutenção preventiva ===")
        manutencao = []
        for produto in self.produtos.values():
            if produto["quantidade_total"] < 10:
                manutencao.append(f"Produto: {produto['descricao']}, Quantidade: {produto['quantidade_total']} unidades - Manutenção Preventiva Necessária!")
                print(f"Produto: {produto['descricao']}, Quantidade: {produto['quantidade_total']} unidades - Manutenção Preventiva Necessária!")
        return manutencao

