# Função para exibir gráfico baseado na escolha do usuário
def exibir_grafico(tipo_grafico, x_labels, y_values, titulo, eixo_x, eixo_y):
    plt.figure(figsize=(10, 6))
    
    if tipo_grafico == 1:  # Se o usuário escolheu "1", cria o gráfico de barras
        plt.bar(x_labels, y_values, color='blue')
    elif tipo_grafico == 2:  # Se o usuário escolheu "2", cria o gráfico de linhas
        plt.plot(x_labels, y_values, marker='o', linestyle='-', color='red')
    
    plt.title(titulo)
    plt.xlabel(eixo_x)
    plt.ylabel(eixo_y)
    plt.xticks(rotation=45)
    plt.tight_layout()
    plt.show()

# Função para exibir tabela com base nas escolhas do usuário
def exibir_tabela(dados_x, dados_y, colunas):
    df = pd.DataFrame({
        colunas[0]: dados_x,
        colunas[1]: dados_y
    })
    print("\nTabela de Dados:")
    print(df.to_string(index=False))

# Função para gerar o dashboard
def dashboard():
    print("=== Relatório de Estoque e Vendas ===")
    
    # Opções para o usuário escolher
    colunas = ["Descrição", "Quantidade", "Valor Total", "Data", "Manutenção Preventiva"]
    print("Escolha as colunas a exibir:")
    for i, coluna in enumerate(colunas, 1):
        print(f"{i}. {coluna}")
    
    escolhas = input("Digite os números das colunas (separados por vírgula, ex: 1,2): ").split(',')
    escolhas = [int(escolha.strip()) for escolha in escolhas]

    # Criando o título do gráfico com base nas escolhas do usuário
    eixo_x = "Produto"
    eixo_y = "Valor Total"
    dados_y = []
    dados_x = []
    labels_data = defaultdict(list)  # Para combinar variáveis como Data com Quantidade ou Valor Total
    
    for produto in estoque.produtos:
        item = estoque.produtos[produto]
        dados_x.append(item['descricao'])  # Vamos usar Descrição no eixo X por padrão
        
        # Preenchendo os dados para o eixo Y
        y_value = 0
        if 2 in escolhas:  # Se o usuário escolheu 'Quantidade'
            y_value += item['quantidade_total']
        if 3 in escolhas:  # Se o usuário escolheu 'Valor Total'
            y_value += item['valor_total']
        if 4 in escolhas:  # Se o usuário escolheu 'Data'
            labels_data[item['data']].append(y_value)  # Combina a data com os valores escolhidos
        
        # Para gráfico de barras ou linhas, precisamos de uma lista de valores para o eixo Y
        dados_y.append(y_value)

    # Perguntar ao usuário que tipo de gráfico ele deseja (agora com números)
    print("\nEscolha o tipo de gráfico:")
    print("1. Gráfico de Barras")
    print("2. Gráfico de Linhas")
    print("3. Exibir Tabela")
    tipo_grafico = int(input("Digite o número da sua escolha (1, 2 ou 3): ").strip())

    # Exibindo o gráfico com base nas escolhas
    if tipo_grafico == 1 or tipo_grafico == 2:
        if tipo_grafico not in [1, 2]:
            print("Escolha inválida. Exibindo gráfico de barras por padrão.")
            tipo_grafico = 1
    
        # Se a escolha for Data, exibe o gráfico com Data
        if 4 in escolhas:
            # Usando as datas no eixo X
            data_labels = list(labels_data.keys())
            data_values = [sum(labels_data[date]) for date in data_labels]  # Soma dos valores por data
            exibir_grafico(tipo_grafico, data_labels, data_values, "Vendas por Data", "Data", "Valor Total")
        else:
            # Se não for Data, exibe gráfico com Descrição
            exibir_grafico(tipo_grafico, dados_x, dados_y, "Comparação de Estoque", eixo_x, eixo_y)
    elif tipo_grafico == 3:
        # Exibindo a tabela
        exibir_tabela(dados_x, dados_y, colunas)

    # Verificando manutenção preventiva se o usuário escolheu essa opção
    if 5 in escolhas:
        manutencao = estoque.verificar_manutencao_preventiva()
        
        # Perguntar ao usuário se ele deseja salvar em arquivo .txt
        salvar = input("\nVocê deseja salvar a manutenção preventiva em um arquivo txt? (s/n): ").strip().lower()
        if salvar == 's':
            # Salvar o resultado em um arquivo .txt
            with open("manutencao_preventiva.txt", "w") as f:
                for item in manutencao:
                    f.write(item + "\n")
            print("\nO arquivo 'manutencao_preventiva.txt' foi salvo com sucesso!")
        else:
            print("\nA manutenção preventiva não foi salva.")

